		// parse your Java file into its visual environment.
		// {{INIT_CONTROLS
		setTitle("Add personal account");
		setModal(true);
		getContentPane().setLayout(null);
		setSize(283, 303);
		setVisible(false);
		JRadioButtonGold.setText("Gold");
		JRadioButtonGold.setActionCommand("0");
		getContentPane().add(JRadioButtonGold);
		JRadioButtonGold.setBounds(36, 0, 84, 24);
		JRadioButtonSilver.setText("Silver");
		JRadioButtonSilver.setActionCommand("1");
		getContentPane().add(JRadioButtonSilver);
		JRadioButtonSilver.setBounds(36, 24, 84, 24);

		JRadioButtonBronze.setText("Bronze");
		JRadioButtonBronze.setActionCommand("2");
		getContentPane().add(JRadioButtonBronze);
		JRadioButtonBronze.setBounds(36, 48, 84, 24);
		
		ButtonGroup group = new ButtonGroup();
	    group.add(JRadioButtonGold);
	    group.add(JRadioButtonSilver);
	    group.add(JRadioButtonBronze);
		
		
		JLabel1.setText("Name");
		getContentPane().add(JLabel1);
		JLabel1.setForeground(java.awt.Color.black);
		JLabel1.setBounds(12, 84, 48, 24);
		JLabel2.setText("Street");
		getContentPane().add(JLabel2);
