		JButton_OK.setText("OK");
		JButton_OK.setActionCommand("OK");
		getContentPane().add(JButton_OK);
		JButton_OK.setBounds(48, 264, 84, 24);
		JButton_Cancel.setText("Cancel");
		JButton_Cancel.setActionCommand("Cancel");
		getContentPane().add(JButton_Cancel);
		JButton_Cancel.setBounds(156, 264, 84, 24);

		JButton_OK.addActionListener(new ActionListener() {

			@Override
			public void actionPerformed(ActionEvent e) {
				int accountType = 0; // checking
				if(JRadioButtonGold.isSelected()) {
					accountType = 0;
				} else if(JRadioButtonSilver.isSelected()) {
					accountType = 1;
				} else if(JRadioButtonBronze.isSelected()) {
					accountType = 2;
				}
				String name=JTextField_NAME.getText(); String
				street=JTextField_STR.getText(); String city=JTextField_CT.getText();
				String zipCode=JTextField_ZIP.getText(); String state=JTextField_ST.getText();
				String email = JTextField_EM.getText();
				CreditCardHolder customer = factory.getCreditCardHolderFactory().createCreditCardHolder(name);
				customer.setAddress(street,city,state,zipCode,"US",email);
				IAccount acc = customer.createAccount(1, 0, accountType);
				acc.addModelChangeListener(new ModelChangeListener<IAccount>() {
					
					@Override
					public void changed(IAccount x) {
						factory.getAccountListFactory().getAccountList().notifyModelChange();
					}
				});
				factory.getAccountListFactory().getAccountList().add(acc);
				dispose();
				
			}
			
		});
		JButton_Cancel.addActionListener(new ActionListener() {

			@Override
			public void actionPerformed(ActionEvent e) {
				dispose();
			}
		});
